import React, { Component } from 'react';
import { Auth } from 'react-components';
import { Redirect, Link } from 'react-router-dom';

import '../../App.css';

class App extends Component {

  state = {
    redirect: false,
  }

  successCB() {
    this.setState({
      redirect: true,
    });
  }

  errorCB() {

  }

  render() {
    const { redirect } = this.state;

    if (redirect) {
      return (
        <Redirect to='/account'/>
      );
    }

    return (
      <div
        className="App"
        style={{
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'center',
          alignItems: 'center',
          padding: 20
        }}
      >
        <h4>Main Account Page</h4>
        <ul>
          <li><Link to='/enable2fa'>Enable Authentication</Link></li>
          <li><Link to='/disable2fa'>Disable Authentication</Link></li>
          <li><Link to='/passwordUpdate'>Password Update</Link></li>
          <li>
            <Auth
              initialDisplayComponent={'logout'}
              successCB={this.successCB}
              errorCB={this.errorCB}
            />
          </li>
        </ul>
      </div>
    );
  }
}
export default App;
