import React from 'react';

import { styles } from '../../utils/styles';

const DisableTwoStep = (props) => {
  return (
    <div style={styles.authInstructions}>
      <p style={styles.instructionStatement}>
        Are you sure you want to disable 2-step verification the account
        <span style={styles.bold}>{props.userEmail}</span>?
      </p>
      <ul styles={styles.radioInputs}>
        <li>
          <label>
            <input
              style={styles.radio}
              type="radio"
              value={true}
              onChange={props.onClickDisable}
              checked={props.disableTwoStep}
            />
            Yes, disable 2-step verification.
          </label>
        </li>
        <li>
          <label>
            <input
              style={styles.radio}
              type="radio"
              value={false}
              onChange={props.onClickDisable}
              checked={!props.disableTwoStep}
            />
            No, keep it enabled.
          </label>
        </li>
      </ul>
    </div>
  );
}

export default DisableTwoStep;