import React from 'react';
import { SlideDown } from 'react-slidedown';
import 'react-slidedown/lib/slidedown.css';

import { styles } from '../../utils/styles';

const PasswordEntry = (props) => {
  return (
    <div style={styles.authInstructions}>
      <p style={styles.instructionStatement}>
        To continue, first verify it's you - {props.userEmail}.
      </p>
      <div
        style={{
          ...styles.labelContainer,
        }}
      >
        <label
          style={{
            ...styles.label,
          }}
          htmlFor="verification_code"
        >
          *Enter your password
        </label>
      </div>
      <input
        style={{
          ...styles.input,
        }}
        type="password"
        name="password"
        placeholder="password"
        value={props.password}
        onChange={props.updatePassword}
        required
      />
      <SlideDown
        closed={!props.error}
      >
        <p
          style={{
            ...styles.error,
          }}
        >
          { props.errorMessage }
        </p>
      </SlideDown>
      <button
        style={{
          ...styles.buttonLink
        }}
        onClick={props.onClickForgotPassword}
      >
        Forgot Password?
      </button>
    </div>
  );
}

export default PasswordEntry;