import React from 'react';
import { SlideDown } from 'react-slidedown';
import 'react-slidedown/lib/slidedown.css';

import { styles } from '../../utils/styles';

const EmailInput = (props) => {
  return (
    <div style={styles.authInstructions}>
      <form
        onSubmit={props.onSubmit}
      >
        <p style={styles.statement}>
          Please enter your email address.  We will send you an email to reset your password.
        </p>

        <div
          style={{
            ...styles.labelContainer,
          }}
        >
          <label
            style={{
              ...styles.label,
            }}
            htmlFor="email"
          >
            *Email
          </label>
        </div>
        <input
          style={{
            ...styles.input,
          }}
          type="email"
          name="email"
          placeholder="email"
          value={props.email}
          onChange={props.updateEmail}
          required
        />
        <SlideDown
          closed={!props.error}
        >
          <p
            style={{
              ...styles.error,
            }}
          >
            { props.errorMessage }
          </p>
        </SlideDown>

        <div
          style={{
            ...styles.bottomLine
          }}
        >
          <span
            style={{
              ...styles.statement
            }}
          >
            Already have an account? {" "}
            <button
              style={{
                ...styles.buttonLink
              }}
              onClick={props.onClickSignIn}
            >
              Sign In.
            </button>
          </span>
          <input
            type='submit'
            style={{
              ...styles.button
            }}
            value='Next'
          />
        </div>
      </form>
    </div>
  );
}

export default EmailInput;