import React from 'react';
import { SlideDown } from 'react-slidedown';
import 'react-slidedown/lib/slidedown.css';

import { styles } from '../../utils/styles';
import PasswordStrengthDisplay from '../Shared/PasswordStrengthDisplay';
import PasswordTooltip from '../Shared/PasswordTooltip';

const OldPassword = (props) => {
  const { passwordMismatchStyle = {} } = props;
  return (
    <div style={styles.authInstructions}>
      <form
        onSubmit={props.onSubmit}
      >
        <p style={styles.statement}>
          Please enter your old password.
        </p>

        <div
          style={{
            ...styles.labelContainer,
          }}
        >
          <label
            style={{
              ...styles.label,
            }}
            htmlFor="password"
          >
            *Password
          </label>
        </div>
        <input
          style={{
            ...styles.input,
            ...passwordMismatchStyle,
          }}
          id="password"
          type="password"
          name="password"
          placeholder="Password"
          value={props.password}
          onChange={props.updatePassword}
          required
        />

        <div
          style={{
            ...styles.bottomLine
          }}
        >
          <span
            style={{
              ...styles.statement
            }}
          >
            <a
              style={{
                ...styles.link
              }}
              href="#"
              onClick={props.onClickExit}
            >
            </a>
          </span>
          <input
            type='submit'
            style={{
              ...styles.button
            }}
            disabled={!props.password || props.password.length === 0}
            value='Next'
          />
        </div>
      </form>
    </div>
  );
}

export default OldPassword;