import React from 'react';
import { SlideDown } from 'react-slidedown';
import 'react-slidedown/lib/slidedown.css';

import { styles } from '../../utils/styles';
import PasswordStrengthDisplay from '../Shared/PasswordStrengthDisplay';
import PasswordTooltip from '../Shared/PasswordTooltip';

const PasswordInput = (props) => {
  const { passwordMismatchStyle = {} } = props;
  return (
    <div style={styles.authInstructions}>
      <form
        onSubmit={props.onSubmit}
      >
        <p style={styles.statement}>
          Please enter your new password.
        </p>

        <ul
          style={{
            ...styles.passwordContainer
          }}
        >
          <li
            style={styles.leftItem}
          >
            <div
              style={{
                ...styles.labelContainer,
              }}
            >
              <label
                style={{
                  ...styles.label,
                }}
                htmlFor="password42"
              >
                *Password
                <PasswordTooltip
                  password={props.password}
                />
              </label>
            </div>
            <input
              style={{
                ...styles.input,
                ...passwordMismatchStyle,
              }}
              id="password42"
              type="password"
              name="password"
              placeholder="Password"
              value={props.password}
              onChange={props.updatePassword}
              required
            />
          </li>
          <li
            style={styles.rightItem}
          >
            <div
              style={{
                ...styles.labelContainer,
              }}
            >
              <label
                style={{
                  ...styles.label,
                }}
                htmlFor="password42confirm"
              >
                *Confirm Password
              </label>
            </div>
            <input
              style={{
                ...styles.input,
                ...passwordMismatchStyle,
              }}
              id="password42confirm"
              type="password"
              name="password_confirmation"
              value={props.passwordConfirmation}
              placeholder="Repeat"
              onChange={props.updatePasswordConfirmation}
              onBlur={props.checkPasswords}
              required
            />
          </li>
        </ul>
        <PasswordStrengthDisplay
          password={props.password}
          passwordMismatch={props.passwordMismatch}
        />
        <div
          style={{
            ...styles.bottomLine
          }}
        >
          <span
            style={{
              ...styles.statement
            }}
          >
            <a
              style={{
                ...styles.link
              }}
              href="#"
              onClick={props.onClickExit}
            >
            </a>
          </span>
          <input
            type='submit'
            style={{
              ...styles.button
            }}
            disabled={props.passwordMismatch || !props.passwordValid}
            value='Next'
          />
        </div>
      </form>
    </div>
  );
}

export default PasswordInput;