import React from 'react';
import { SlideDown } from 'react-slidedown';
import 'react-slidedown/lib/slidedown.css';

import { styles } from '../../utils/styles';

const ErrorMessage = (props) => {

  return (
    <SlideDown
      closed={!props.error}
    >
      <p
        style={{
          ...styles.error,
          ...props.errorStyle
        }}
      >
        { props.errorMessage }
      </p>
    </SlideDown>
  );
}

export default ErrorMessage;