import React from 'react';
import Responsive from 'react-responsive';

export const Desktop = props => <Responsive {...props} minWidth={992} />;
export const Tablet = props => <Responsive {...props} minWidth={768} maxWidth={991} />;
export const Mobile = props => <Responsive {...props} maxWidth={767} />;
export const Default = props => <Responsive {...props} minWidth={768} />;
export const IPhone5 = props => <Responsive {...props} maxWidth={320} />;
export const IPhone678 = props => <Responsive {...props} maxWidth={375} />;
export const IPhone678Plus = props => <Responsive {...props} maxWidth={414} />;
export const GalaxyS5 = props => <Responsive {...props} maxWidth={360} />;
